def checkOffset(offset):
	x = 0
	treesHit = 0
	lineNum = -1
	for line in slope:
		lineNum += 1
		if (lineNum % offset[1] != 0):
			continue
		if line[x] == "#":
			treesHit += 1
		x = (x + offset[0]) % len(line)
	return treesHit

print("Part 1:")
